class Greeter
  def initialize(name)
    @name = name.capitalize
  end

  def salute
    puts &#34;Hello #&amp;#123;@name&amp;#125;!&#34;
  end
end

g = Greeter.new(&#34;world&#34;)
g.salute
&lt;/code&gt;&lt;/pre&gt;
